import React, { useEffect, useState } from "react"
import { Table } from "antd"
import { connect } from "react-redux"

// @custom-component-imports ------------------------------------------------------------------------ 

    import DataTable from 'react-data-table-component';
    import DataTableExtensions from 'react-data-table-component-extensions';
    import 'react-data-table-component-extensions/dist/index.css';

    import Header from "../../component/Header/Header"
    import { PageContainer } from "../../component/Xcomponent"

// @actions-imports ----------------------------------------------------------------------------------

    import { getOrderList, getOrderListReset } from "../../action/getOrderListAction"
    import { getCustomerList, getCustomerListReset } from "../../action/getCustomerListAction"

    const columns = [
        {
          name: 'Title',
          selector: 'title',
          sortable: true,
        },
        {
          name: 'Director',
          selector: 'director',
          sortable: true,
        },
        {
          name: 'Genres',
          selector: 'genres',
          sortable: true,
          cell: d => <span>{d.genres.join(', ')}</span>,
        },
        {
          name: 'Year',
          selector: 'year',
          sortable: true,
        },
    ];
    const data = [
        {
          title: 'Beetle Juice',
          year: '1988',
          genres: [
            'Comedy',
            'Fantasy',
          ],
          director: 'Tim Burton',
        },
        {
          id: 2,
          title: 'The Cotton Club',
          year: '1984',
          runtime: '127',
          genres: [
            'Crime',
            'Drama',
            'Music',
          ],
          director: 'Francis Ford Coppola',
        }];

//-----------------------------------------------------------------------------------------------------

const CustomerList = props => {

    const { getCustomerList, getCustomerListState } = props
    const [tableData, setTableData] = useState([])

    const columns = [
        {
            title: 'Customer Name',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: 'Email',
            dataIndex: 'email',
            key: 'email',
            render: email => <span>{email}</span>
        },
        {
            title: 'Register Date',
            dataIndex: 'created_at',
            key: 'created_at',
            render: (created_at) => {
                var date = new Date(created_at);
                var month = date.getMonth() + 1;//months (0-11)
                var day = date.getDate();//day (1-31)
                var year = date.getFullYear();

                var formattedDate = month + "/" + day + "/" + year;
                return (
                    <span>{formattedDate}</span>
                )
            }
        },
    ];

    /* callbacks */
    useEffect(() => {
        getCustomerList()
    }, [])

    useEffect(() => {
        if (getCustomerListState.apiState === "success") {


        console.log("hello", getCustomerListState.list);


            let tableData = []
            getCustomerListState.list.map(row => {
                tableData.push({
                    key: row.id,
                    name: row.name,
                    email: row.email,
                    created_at: row.created_at,
                    id: row.id,
                })
            })
            setTableData(tableData)
        }
    }, [getCustomerListState])


    return (
        <React.Fragment>
            <Header title="Customer List"/>
            <PageContainer>
                <Table dataSource={tableData} columns={columns} size="small" pagination={false} />

                <DataTableExtensions {...tableData} >
                    <DataTable
                        noHeader
                        defaultSortField="id"
                        defaultSortAsc={false}
                        pagination
                        highlightOnHover
                    />
                </DataTableExtensions>
            </PageContainer>
        </React.Fragment>
    )
}
const mapStateToProps = (state) => ({
    getOrderListState    : state.getOrderList,
    getCustomerListState : state.getCustomerList,
})

const mapDispatchToProps = (dispatch) => ({
    getOrderList:         (params) => dispatch(getOrderList(params)),
    getOrderListReset:    ()       => dispatch(getOrderListReset()),
    getCustomerList:      (params) => dispatch(getCustomerList(params)),
    getCustomerListReset: ()       => dispatch(getCustomerListReset()),
})

export default connect(mapStateToProps, mapDispatchToProps)(CustomerList)
