import React, { useEffect, useState, createContext } from "react"
import { connect } from "react-redux"

// @custom-component-imports ------------------------------------------------------------------------ 

    import DataTable from 'react-data-table-component';
    import DataTableExtensions from 'react-data-table-component-extensions';
    import 'react-data-table-component-extensions/dist/index.css';
    import {  Button, Modal, Space  } from 'antd'
    import { ExclamationCircleOutlined } from '@ant-design/icons';

    import Header from "../../component/Header/Header"
    import { PageContainer } from "../../component/Xcomponent"


// @actions-imports ----------------------------------------------------------------------------------

    import { getOrderList, getOrderListReset } from "../../action/getOrderListAction"
    import { getCustomerList, getCustomerListReset } from "../../action/getCustomerListAction"

//-----------------------------------------------------------------------------------------------------

const CustomerList = props => {

    const { getCustomerList, getCustomerListState } = props
    const [modal, contextHolder]    = Modal.useModal();
    const [tableData, setTableData] = useState([])

    const ReachableContext   = createContext(null);
    const UnreachableContext = createContext(null);
    const config = {
        title: 'Use Hook!',
        content: (
          <>
            <ReachableContext.Consumer>{(name) => `Reachable: ${name}!`}</ReachableContext.Consumer>
            <br />
            <UnreachableContext.Consumer>{(name) => `Unreachable: ${name}!`}</UnreachableContext.Consumer>
          </>
        ),
      };

    const [columns,setColumns] = useState([{
            name: 'Customer Name',
            selector: data => data.name,
            sortable: true,
        },{
            name: 'Customer Email',
            selector: data => data.email,
            sortable: true,
        },{
            name: 'Customer Contact No.',
            selector: data => data.contact_no,
            sortable: true,
        },{
            name: 'Customer Status',
            selector: data => data.status,
            sortable: true,
        },{
            name: 'Register Date',
            selector: data => data.date,
            sortable: true,
        },{
            name: 'Action',
            selector: data => <Button onClick={() => { modal.confirm(config) }}>Deactivate</Button>,
            sortable: true,
        }
    ])



/* callbacks -------------------------------------------------------------------------------------------------*/

    useEffect(() => { getCustomerList()}, [])
    useEffect(() => {

        if (getCustomerListState.apiState === "success") {

            const customerList = getCustomerListState.list.map(data => {

                const serverDate = date => date.toISOString().slice(0, 10);

                data.created_at = serverDate(new Date(data.created_at))

                return  {
                    name   : data.name,
                    email  : data.email,
                    date   : data.created_at,
                    status : data.status,
                    contact_no: data.contact_no,
                }
            })

            setTableData(customerList);
        }
    }, [getCustomerListState])

    const LocalizedModal = () => {
        const [visible, setVisible] = useState(false);

        const showModal = () => {
          setVisible(true);
        };

        const hideModal = () => {
          setVisible(false);
        };

        return (
          <>
            <Button type="primary" onClick={showModal}>
              Show Modal
            </Button>
            <Modal
              title="Modal"
              visible={visible}
              onOk={hideModal}
              onCancel={hideModal}
              okText="确认"
              cancelText="取消"
            >
              <p>Bla bla ...</p>
              <p>Bla bla ...</p>
              <p>Bla bla ...</p>
            </Modal>
          </>
        );
      };

    const confirm = () => {
        Modal.confirm({
          title: 'Confirm',
          icon: <ExclamationCircleOutlined />,
          content: 'Bla bla ...',
          okText: '确认',
          cancelText: '取消',
        });
      };



/* ----------------------------------------------------------------------------------------------------------*/

    return (
        <React.Fragment>
            <Header title="Customer List"/>
            <PageContainer>

            <LocalizedModal />
            <Button onClick={confirm}>Confirm</Button>

                <DataTableExtensions print={false} columns={columns} data={tableData} >
                    <DataTable
                        noHeader
                        defaultSortField="id"
                        defaultSortAsc={false}
                        pagination
                        highlightOnHover
                    />
                </DataTableExtensions>
            </PageContainer>
        </React.Fragment>
    )
}
const mapStateToProps = (state) => ({
    getOrderListState    : state.getOrderList,
    getCustomerListState : state.getCustomerList,
})

const mapDispatchToProps = (dispatch) => ({
    getOrderList:         (params) => dispatch(getOrderList(params)),
    getOrderListReset:    ()       => dispatch(getOrderListReset()),
    getCustomerList:      (params) => dispatch(getCustomerList(params)),
    getCustomerListReset: ()       => dispatch(getCustomerListReset()),
})

export default connect(mapStateToProps, mapDispatchToProps)(CustomerList)
